			}

			if ($this->aBlog !== null) {
				if ($this->aBlog->isModified() || $this->aBlog->isNew()) {
					$affectedRows += $this->aBlog->save($con);
				}
				$this->setBlog($this->aBlog);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = PostPeer::POST_ID;
