	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collComentarios) {
				foreach ((array) $this->collComentarios as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collRespuestas) {
				foreach ((array) $this->collRespuestas as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collNotasBoletins) {
				foreach ((array) $this->collNotasBoletins as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collComentarios = null;
		$this->collRespuestas = null;
		$this->collNotasBoletins = null;
		$this->aTipoPost = null;
		$this->asfGuardUser = null;
		$this->aBlog = null;
	}

	/**
	 * Catches calls to virtual methods
	 */
	public function __call($name, $params)
	{
		// symfony_behaviors behavior
		if ($callable = sfMixer::getCallable('BasePost:' . $name))
		{
		  array_unshift($params, $this);
		  return call_user_func_array($callable, $params);
		}

		if (preg_match('/get(\w+)/', $name, $matches)) {
			$virtualColumn = $matches[1];
			if ($this->hasVirtualColumn($virtualColumn)) {
				return $this->getVirtualColumn($virtualColumn);
			}
			// no lcfirst in php<5.3...
			$virtualColumn[0] = strtolower($virtualColumn[0]);
			if ($this->hasVirtualColumn($virtualColumn)) {
				return $this->getVirtualColumn($virtualColumn);
			}
		}
		return parent::__call($name, $params);
	}

